mapper.update:
update comment set
    content=#{content}, good=#{good}, bad=#{bad}
where id=#{id}

mapper.select:
select id, member_id, movie_id, content, indate, good, bad
from comment
where movie_id=#{movieId}

select id, member_id, movie_id, content, indate, good, bad
from comment
where member_id=#{memberId}

select id, member_id, movie_id, content, indate, good, bad
from comment
where id=#{id}
